import React from 'react';
import { connect } from 'react-redux';
import {
  FetchSingleData,
  SetActiveIdentifier,
} from '../../Redux/Actions/actions.js';
import ImageLoader from 'react-image-file';

class MediaFilesList extends React.PureComponent {
  render() {
    if (this.props.responseDataList !== '') {
      var elements = [];
      var element;
      var header = (
        <>
          <thead>
            <tr>
              <th>Mediafile</th>
            </tr>
          </thead>
          <button
            onClick={() => {
              this.props.getImage(1);
            }}
          >
            GetImage
          </button>
        </>
      );

      var arr = [];
      for (let i = 0; i < this.props.responseDataList.length; i++) {
        arr = arr.concat(this.props.responseDataList[i]);
      }

      for (let i = 0; i < this.props.responseDataList.length; i++) {
        element = '';
        element = (
          <tr className="dataTable">
            <img src={this.props.responseDataList[i]} alt="test" />
          </tr>
        );
        elements = elements.concat(element);
      }
    }

    if (this.props.responseDataList !== '') {
      return (
        <>
          <div className="dataTable">
            <table className="tableStyle">
              {header}
              <tbody>{elements}</tbody>
            </table>
          </div>
        </>
      );
    } else {
      return (
        <>
          <p>There is no Data in DB...</p>
        </>
      );
    }
  }
}

function mapStateToProps(state) {
  return {
    responseDataList: state.responseDataList,

    activeId: state.activeId,
    activeMenu: state.activeMenu,
  };
}

function mapDispatchToProps(dispatch) {
  return {
    getImage: (id, activeMenu) => dispatch(FetchSingleData(id, activeMenu)),
    setActiveId: (id) => dispatch(SetActiveIdentifier(id)),
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(MediaFilesList);
