import React from 'react';
import { connect } from 'react-redux';
import 'react-responsive-modal/styles.css';
import { Modal } from 'react-responsive-modal';
import { SetCreateModalState, AddData } from '../../Redux/Actions/actions.js';
import Select from 'react-select';

class CreateServiceModal extends React.PureComponent {
  render() {
    var serviceInfoArea;
    var actionButtons;
    var serviceToCreate = {
      procedureName: '',
      procedureDescription: '',
      timeAmount: '',
      procedurePrice: '',
      procedureType: '',
      materials: [],
    };

    var procedureTypesList = [];
    // this.props.responseDataList.procedureTypesList.forEach((element) => {
    //   procedureTypesList.push({
    //     value: element.value,
    //     label: element.text,
    //   });
    // });

    var materialList = [];
    // this.props.responseDataList.materialsList.forEach((element) => {
    //   materialList.push({
    //     value: element.value,
    //     label: element.text,
    //   });
    // });

    actionButtons = (
      <div className="confirmUserInfo">
        <div>
          <button
            className="modalButton"
            onClick={() => {
              this.props.setCreateModalState();
              this.props.onCreate(this.props.activeMenu, serviceToCreate);
            }}
          >
            Create
          </button>
        </div>
      </div>
    );

    serviceInfoArea = (
      <div className="profileText">
        <div className="profileTextItem">
          <div className="profileTextItem-left">
            <p>Procedure name:</p>
          </div>
          <div className="profileTextItem-right">
            <input
              type="text"
              placeholder="Procedure name..."
              onChange={(e) => {
                serviceToCreate.procedureName = e.target.value;
              }}
            />
          </div>
        </div>

        <div className="profileTextItem">
          <div className="profileTextItem-left">
            <p>Procedure description:</p>
          </div>
          <div className="profileTextItem-right">
            <input
              type="text"
              placeholder="Procedure description..."
              onChange={(e) => {
                serviceToCreate.procedureDescription = e.target.value;
              }}
            />
          </div>
        </div>

        <div className="profileTextItem">
          <div className="profileTextItem-left">
            <p>Time amount:</p>
          </div>
          <div className="profileTextItem-right">
            <input
              type="text"
              placeholder="Time amount..."
              onChange={(e) => {
                serviceToCreate.timeAmount = e.target.value;
              }}
            />
          </div>
        </div>

        <div className="profileTextItem">
          <div className="profileTextItem-left">
            <p>Procedure price:</p>
          </div>
          <div className="profileTextItem-right">
            <input
              type="email"
              placeholder="Procedure price..."
              onChange={(e) => {
                serviceToCreate.procedurePrice = e.target.value;
              }}
            />
          </div>
        </div>

        <div className="profileTextItem">
          <div className="profileTextItem-left">
            <p>Procedure type:</p>
          </div>
          <div className="profileTextItem-right">
            <Select
              maxMenuHeight={100}
              options={procedureTypesList}
              onChange={(data) => {
                this.props.procedures.forEach((procedure) => {
                  if (procedure.procedureId === data.value) {
                    serviceToCreate.procedureType = procedure;
                  }
                });
              }}
            />
          </div>
        </div>

        <div className="profileTextItem">
          <div className="profileTextItem-left">
            <p>Materials:</p>
          </div>
          <div className="profileTextItem-right">
            <Select
              isMulti={true}
              maxMenuHeight={100}
              options={materialList}
              onChange={(data) => {
                this.props.materials.forEach((material) => {
                  if (material.materialId === data.value) {
                    serviceToCreate.materials.push(material);
                  }
                });
              }}
            />
          </div>
        </div>
      </div>
    );

    return (
      <Modal
        styles={{
          modal: {
            background: 'wheat',
            padding: '40px',
            fontFamily: 'Play',
            alignItems: 'center',
            textAlign: 'center',
            fontSize: '45px',
            border: '3px solid black',
            borderRadius: '10px',
          },
        }}
        open={this.props.createModalOpen}
        center={true}
        showCloseIcon={false}
        onOverlayClick={this.props.setCreateModalState}
      >
        <>
          <div className="profileBody">{serviceInfoArea}</div>
          {actionButtons}
        </>
      </Modal>
    );
  }
}

function mapStateToProps(state) {
  return {
    createModalOpen: state.createModalOpen,
    responseDataList: state.responseDataList,
    singleResponseData: state.singleResponseData,
    activeId: state.activeId,
    activeMenu: state.activeMenu,
  };
}

function mapDispatchToProps(dispatch) {
  return {
    setCreateModalState: () => dispatch(SetCreateModalState()),
    onCreate: (activeMenu, data) => dispatch(AddData(activeMenu, data)),
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(CreateServiceModal);
