import React, { useState } from 'react';
import 'react-responsive-modal/styles.css';
import { Modal } from 'react-responsive-modal';
import { EmployeeAutorization } from '../Redux/Actions/actions.js';
import { connect } from 'react-redux';

class WorkHereModal extends React.PureComponent {
  render() {
    return (
      <div>
        <button onClick={onOpenModal}>Work here?</button>
        <Modal open={modalOpen} onClose={onCloseModal} center>
          <div>
            <p>Email: </p>
            <input type="text" id="employeeEmail" />
            <p>Password: </p>
            <input type="text" id="employeePassword" />
            <button
              onClick={() => {
                this.props.onAuth(
                  document.getElementById('employeeEmail').value,
                  document.getElementById('employeePassword').value,
                );
              }}
            >
              Login
            </button>
          </div>
        </Modal>
      </div>
    );
  }
}

function mapStateToProps(state) {
  return {
    modalOpen: state.modalOpen,
  };
}

function mapDispatchToProps(dispatch) {
  return {
    onAuth: (email, password) =>
      dispatch(EmployeeAutorization(email, password)),
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(WorkHereModal);
