import React from 'react';
import { connect } from 'react-redux';
import * as actions from '../Redux/Actions/actions';
import ManagementPage from './StuffComponents/Management';
import { LoginModal } from './Modals/LoginModal';
import StyleChange from '../Functions/ChangeStyles';

class NavigationBar extends React.PureComponent {
  render() {
    if (this.props.employeeToken === '') {
      return (
        <nav className="navigationBar">
          <ul>
            <li>
              <button
                className="sideBarItem"
                id="ourServices"
                onClick={(e) => {
                  StyleChange(e.target.id);
                }}
              >
                Our services
              </button>
            </li>
            <li>
              <button
                className="sideBarItem"
                id="ourStaff"
                onClick={(e) => {
                  StyleChange(e.target.id);
                }}
              >
                Our staff
              </button>
            </li>
            <li>
              <button
                className="sideBarItem"
                id="feedback"
                onClick={(e) => {
                  StyleChange(e.target.id);
                }}
              >
                Feedback
              </button>
            </li>
            <li>
              <button
                className="sideBarItem"
                id="reviews"
                onClick={(e) => {
                  StyleChange(e.target.id);
                }}
              >
                Reviews
              </button>
            </li>
            <li>
              <button
                className="sideBarItem"
                id="aboutUs"
                onClick={(e) => {
                  StyleChange(e.target.id);
                }}
              >
                About us
              </button>
            </li>
            <li>
              <LoginModal />
            </li>
          </ul>
          <button className="booking">Book now</button>
        </nav>
      );
    } else {
      return <ManagementPage />;
    }
  }
}

function mapStateToProps(state) {
  return {
    employeeToken: state.employeeToken,
  };
}

function mapDispatchToProps(dispatch) {
  return {
    getClients: () => dispatch(actions.ClientsFeetchData()),
    getStaff: () => dispatch(actions.StaffsFeetchData()),
    getMaterials: () => dispatch(actions.MaterialsFeetchData()),
    getServices: () => dispatch(actions.ServicesFeetchData()),
    getOrders: () => dispatch(actions.OrdersFeetchData()),
    getSchedules: () => dispatch(actions.SchedulesFeetchData()),
    getPromos: () => dispatch(actions.PromosFeetchData()),
    getProfiles: () => dispatch(actions.ProfilesFeetchData()),
    getFeedbacks: () => dispatch(actions.FeedbacksFeetchData()),
    getGallery: () => dispatch(actions.MediaFilesFeetchData()),
    getFiles: () => dispatch(actions.MediaFilesFeetchData()),
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(NavigationBar);
