import React from 'react';
import { connect, connectAdvanced } from 'react-redux';
import Dropdown from 'react-dropdown';
import 'react-dropdown/style.css';

class RolesDropdown extends React.PureComponent {
  render() {
    var roles = [];
    var defaultOption = this.props.singleResponseData.role;
    this.props.responseDataList.roles.array.forEach((element) => {
      roles.push(element.name);
    });

    return (
      <Dropdown
        options={roles}
        onChange={this._onSelect}
        value={defaultOption}
        placeholder="Select an option"
      />
    );
  }
}

function mapStateToProps(state) {
  return {
    responseDataList: state.responseDataList,
    singleResponseData: state.singleResponseData,
  };
}

export default connect(mapStateToProps, null)(RolesDropdown);
