import React from 'react';
import { connect } from 'react-redux';
import * as actions from '../Redux/Actions/actions';
import ManagementPage from './DataTablesComponents/Management';
import { LoginModal } from './ModalWindowsComponents/LoginModal';
import StyleChange from '../Functions/ChangeStyles';
import { enableRipple } from '@syncfusion/ej2-base';
import { TimePickerComponent } from '@syncfusion/ej2-react-calendars';
// enable ripple effect
enableRipple(true);

class NavigationBar extends React.PureComponent {
  render() {
    var time = new Date('8/3/2017 10:00 AM');
    var minTime = new Date('8/3/2017 7:00 AM');
    var maxTime = new Date('8/3/2017 4:00 PM');
    if (this.props.employeeToken === '') {
      return (
        <nav className="navigationBar">
          <ul>
            <li>
              <button
                className="managementMenuButton"
                id="ourServices"
                onClick={(e) => {
                  StyleChange(e.target.id);
                }}
              >
                Our services
              </button>
            </li>
            <li>
              <button
                className="managementMenuButton"
                id="ourStaff"
                onClick={(e) => {
                  StyleChange(e.target.id);
                }}
              >
                Our staff
              </button>
            </li>
            <li>
              <button
                className="managementMenuButton"
                id="feedback"
                onClick={(e) => {
                  StyleChange(e.target.id);
                }}
              >
                Feedback
              </button>
            </li>
            <li>
              <button
                className="managementMenuButton"
                id="reviews"
                onClick={(e) => {
                  StyleChange(e.target.id);
                }}
              >
                Reviews
              </button>
            </li>
            <li>
              <button
                className="managementMenuButton"
                id="aboutUs"
                onClick={(e) => {
                  StyleChange(e.target.id);
                }}
              >
                About us
              </button>
            </li>
            <li>
              <TimePickerComponent
                id="timepicker"
                value={time}
                minTime={minTime}
                maxTime={maxTime}
                step={60}
                format={'HH:mm'}
              />
            </li>
            <li>
              <LoginModal />
            </li>
          </ul>
          <button className="booking">Book now</button>
        </nav>
      );
    } else {
      return <ManagementPage />;
    }
  }
}

function mapStateToProps(state) {
  return {
    employeeToken: state.employeeToken,
  };
}

function mapDispatchToProps(dispatch) {
  return {
    getAll: () => dispatch(actions.FeetchAllData()),
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(NavigationBar);
