import React from 'react';
import { connect } from 'react-redux';
import 'react-responsive-modal/styles.css';
import { Modal } from 'react-responsive-modal';
import { SetCreateModalState, AddData } from '../../Redux/Actions/actions.js';
import Select from 'react-select';

class CreateEmployeeModal extends React.PureComponent {
  render() {
    var employeeInfoArea;
    var actionButtons;
    var employeeToCreate = {
      firstName: '',
      lastName: '',
      phoneNumber: '',
      password: '',
      email: '',
      specialization: '',
      qualification: '',
      role: '',
      hireDate: '',
      schedule: null,
      scheduleId: null,
      profile: null,
      profileId: null,
      orders: null,
    };

    var roles = [];
    this.props.responseDataList.roles.forEach((element) => {
      roles.push({ value: element.value, label: element.name });
    });

    var qualifications = [];
    this.props.responseDataList.qualification.forEach((element) => {
      qualifications.push({ value: element.value, label: element.name });
    });

    var specializations = [];
    this.props.responseDataList.specialization.forEach((element) => {
      specializations.push({ value: element.value, label: element.name });
    });

    actionButtons = (
      <div className="confirmUserInfo">
        <div>
          <input type="file" />
        </div>
        <div>
          <button
            className="modalButton"
            onClick={() => {
              this.props.setCreateModalState();
              this.props.onCreate(this.props.activeMenu, employeeToCreate);
            }}
          >
            Create
          </button>
        </div>
      </div>
    );

    employeeInfoArea = (
      <div className="profileText">
        <div className="profileTextItem">
          <div className="profileTextItem-left">
            <p>First name:</p>
          </div>
          <div className="profileTextItem-right">
            <input
              type="text"
              placeholder="First name..."
              onChange={(e) => {
                employeeToCreate.firstName = e.target.value;
              }}
            />
          </div>
        </div>

        <div className="profileTextItem">
          <div className="profileTextItem-left">
            <p>Last name:</p>
          </div>
          <div className="profileTextItem-right">
            <input
              type="text"
              placeholder="Last name..."
              onChange={(e) => {
                employeeToCreate.lastName = e.target.value;
              }}
            />
          </div>
        </div>

        <div className="profileTextItem">
          <div className="profileTextItem-left">
            <p>Hired:</p>
          </div>
          <div className="profileTextItem-right">
            <input
              type="date"
              placeholder="Hire date..."
              onChange={(e) => {
                employeeToCreate.hireDate = e.target.value;
                console.log(employeeToCreate.hireDate);
              }}
            />
          </div>
        </div>

        <div className="profileTextItem">
          <div className="profileTextItem-left">
            <p>Phone:</p>
          </div>
          <div className="profileTextItem-right">
            <input
              type="text"
              placeholder="Phone number..."
              onChange={(e) => {
                employeeToCreate.phone = e.target.value;
              }}
            />
          </div>
        </div>

        <div className="profileTextItem">
          <div className="profileTextItem-left">
            <p>Email:</p>
          </div>
          <div className="profileTextItem-right">
            <input
              type="text"
              placeholder="E-mail..."
              onChange={(e) => {
                employeeToCreate.email = e.target.value;
              }}
            />
          </div>
        </div>

        <div className="profileTextItem">
          <div className="profileTextItem-left">
            <p>Role:</p>
          </div>
          <div className="profileTextItem-right">
            <Select
              onChange={(data) => {
                employeeToCreate.role = data.value;
              }}
              options={roles}
            />
          </div>
        </div>

        <div className="profileTextItem">
          <div className="profileTextItem-left">
            <p>Specialization:</p>
          </div>
          <div className="profileTextItem-right">
            <Select
              options={specializations}
              onChange={(data) => {
                employeeToCreate.specialization = data.value;
              }}
            />
          </div>
        </div>

        <div className="profileTextItem">
          <div className="profileTextItem-left">
            <p>Qualification:</p>
          </div>
          <div className="profileTextItem-right">
            <Select
              options={qualifications}
              onChange={(data) => {
                employeeToCreate.qualification = data.value;
              }}
            />
          </div>
        </div>
      </div>
    );

    return (
      <Modal
        styles={{
          modal: {
            background: 'wheat',
            padding: '40px',
            fontFamily: 'Play',
            alignItems: 'center',
            textAlign: 'center',
            fontSize: '45px',
            border: '3px solid black',
            borderRadius: '10px',
          },
        }}
        open={this.props.createModalOpen}
        center={true}
        showCloseIcon={false}
        onOverlayClick={this.props.setCreateModalState}
      >
        <>
          <div className="profileBody">{employeeInfoArea}</div>
          {actionButtons}
        </>
      </Modal>
    );
  }
}

function mapStateToProps(state) {
  return {
    createModalOpen: state.createModalOpen,
    responseDataList: state.responseDataList,
    singleResponseData: state.singleResponseData,
    activeId: state.activeId,
    activeMenu: state.activeMenu,
  };
}

function mapDispatchToProps(dispatch) {
  return {
    setCreateModalState: () => dispatch(SetCreateModalState()),
    onCreate: (activeMenu, data) => dispatch(AddData(activeMenu, data)),
  };
}

export default connect(
  mapStateToProps,
  mapDispatchToProps,
)(CreateEmployeeModal);
